                                        $('#scroll_container').scrollTop(0);
                                    } else if (pageskips == 2) {

                                        var product_listtop = $cstlayer.css('top').replace('px', '');
                                        var father = $cstlayer.attr('fatherid') || '';
                                        if (father) {
                                            var father_top = $('#' + father).css('top').replace('px', '');
                                            product_listtop = parseInt(product_listtop) + parseInt(father_top);
                                            if ($("#" + father).length > 0) {
                                                var fathers = $("#" + father).attr('fatherid') || '';
                                                if (fathers) {
                                                    var father_tops = $('#' + fathers).css('top').replace('px', '');
                                                    product_listtop = parseInt(product_listtop) + parseInt(father_tops);
                                                }
                                            }
                                        }

                                        if (product_listtop) {
                                            $('#scroll_container').scrollTop(product_listtop);
                                        }
                                    }
                                }
                                return false;
                            }
                        });
                        if ($.cookie('wp_layer_page_' + layerid) && $.cookie('wp_layer_page_' + layerid) != 1 && !$.browser.msie) {
                            $pglnker.find('a:first').trigger('click', $.cookie('wp_layer_page_' + layerid));
                        }
                        // About input
                        $pglnker.find(':input').each(function (i, dom) {
                            var $input = $(this), ent = pgid = '', fnc;
                            switch ($input.attr("type")) {
                                case "text":
                                    ent = 'keyup';
                                    fnc = function () {
                                        pgid = this.value = this.value.replace(/(?:\b0|[^\d+])/i, '');
                                        return false;
                                    };
                                    break;
                                case "button":
                                    ent = 'click';
                                    fnc = function () {
                                        if (pgid.length && /^[1-9]{1}\d*$/.test(pgid)) {
                                            var maxpg = _int($pglnker.find('span.total').html());
                                            if (!maxpg) maxpg = 1;
                                            $pglnker.find('a').triggerHandler('click', [Math.min(pgid, maxpg)]);
                                        }

                                        function _int(numString) {
                                            var number = parseInt(numString);
                                            if (isNaN(number)) return 0;
                                            return number;
                                        }

                                        return false;
                                    };
                                    break;
                            }
                            if (fnc && $.isFunction(fnc)) $input[ent](fnc);
                        });
                    });
                    $(window).load(function () {
                        $('#layer046FAC2DFDF48A3601F67B0DA4382A51').find(".wp-pager_link").css({'position': 'relative', 'bottom': 'auto', 'width': '100%'});
                        wp_heightAdapt($('#layer046FAC2DFDF48A3601F67B0DA4382A51'));
                        var cheight = $('#layer046FAC2DFDF48A3601F67B0DA4382A51').find(".wp-product_content").height();
                        var oulheight = $('#layer046FAC2DFDF48A3601F67B0DA4382A51').find(".product_list-layer046FAC2DFDF48A3601F67B0DA4382A51").height();
                        var olkheight = $('#layer046FAC2DFDF48A3601F67B0DA4382A51').find(".wp-pager_link").outerHeight();
                        if (cheight > oulheight + olkheight) {
                            $('#layer046FAC2DFDF48A3601F67B0DA4382A51').find(".wp-pager_link").css({'position': 'absolute', 'bottom': '0px', 'width': '100%'})
                        }
                    })
                </SCRIPT>

